import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import Select from 'react-select';
import Filter from "./filter.js";


class App extends React.Component{

    constructor(props){
      super(props);

      // this.handleChange=this.handleChange.bind(this);
      // this.handleSubmit=this.handleSubmit.bind(this);
      this.handleOnSelect=this.handleOnSelect.bind(this);
      this.state={
        // view: false,
        // value:'',
        selected:[],
        count:0
      };
    }

    handleOnSelect(selection){
      console.log(this.state.selected)
      console.log(this.state.count)
      // if(!selection){
      //   alert('enter valild input')
      // }else if(this.state.selected.indexOf(selection)>-1){
      //   alert('removing')

      // }
      this.setState((prevState)=>{

        return{
          selected:selection,
          count:prevState.count+1    
        };
      })

    }

    // handleChange(e){
    //   this.setState({value: e.target.value});
    // }

    // handleSubmit(e){
    //   alert(this.state.value);
    //   e.preventDefault();
    // }
    render(){
        const options=[
          {value: 'chocolate',label:'Chocolate'},
          {value:'vanilla',label:'Vanilla'},
          {value:'banana',label:'Banana'}

        ]
        return(
            <div>
            <h1>Meal Preferences Application</h1>
            <Filter name='cusine' choices= {[ {value: 'chocolate',label:'Chocolate'},
          {value:'vanilla',label:'Vanilla'},
          {value:'banana',label:'Banana'}]}></Filter>
            </div>
        );
    }
}

export default App;
