//create component called CurrentLocation where all functionality to pick browsers location lies 
import React from 'react'
import ReactDOM from 'react-dom'
import Geocode from 'react-geocode'

const mapStyles={
    map:{
        position :'absolute',
        width:'100%',
        height: '100%'
    }
};

Geocode.setApiKey('AIzaSyD-a_aMfM44H43DL1gkBccsYjcYgZTZWQk');

export class CurrentLocation extends React.Component{
    constructor(props){
        super(props);//make it stateful
        const {lat,lng,name}=this.props.initialCenter;
        this.state={
            currentLocation:{
                lat: lat,
                lng:lng,
                name:name
            },
            showingInfoWindow:false
        };
    }

    componentDidMount(){
        if(this.props.centerAroundCurrentLocation){
            if(navigator && navigator.geolocation){
                navigator.geolocation.getCurrentPosition(pos =>{
                    const coords= pos.coords;
                    this.setState({
                        currentLocation:{
                            lat: coords.latitude,
                            lng:coords.longitude,
                            name_loc:  Geocode.fromLatLng(coords.latitude,coords.longitude).then(
                                response=>{
                                    this.state.currentLocation.name_loc=response.results[0].formatted_address;   
                                },
                                error =>{
                                    console.log(error);
                                }
                            )
                        }
                    });
                });

            }
        }
        this.loadMap();
    }

    componentDidUpdate(prevProps,prevState){
        if(prevProps.google!== this.props.google){
            this.loadMap();
        }
        if(prevState.currentLocation !== this.state.currentLocation){
            this.recenterMap();
        }
    }

    recenterMap(){
        const map=this.map;
        const current =this.state.currentLocation;

        const google=this.props.google;
        const maps=google.maps;

        if(map){
            let center=new maps.LatLng(current.lat,current.lng,current.name_loc);
            map.panTo(center);
        }
    }

    initAutocomplete() {
        var map = this.map;

        // Create the search box and link it to the UI element.
        var input = document.getElementById('pac-input');
        var searchBox = this.google.maps.places.SearchBox(input);
        map.controls[this.google.maps.ControlPosition.TOP_LEFT].push(input);

        // Bias the SearchBox results towards current map's viewport.
        map.addListener('bounds_changed', function() {
          searchBox.setBounds(map.getBounds());
        });

        var markers = [];
        // Listen for the event fired when the user selects a prediction and retrieve
        // more details for that place.
        searchBox.addListener('places_changed', function() {
          var places = searchBox.getPlaces();

          if (places.length == 0) {
            return;
          }

          // Clear out the old markers.
          markers.forEach(function(marker) {
            marker.setMap(null);
          });
          markers = [];

          // For each place, get the icon, name and location.
          var bounds =  this.google.maps.LatLngBounds();
          places.forEach(function(place) {
            if (!place.geometry) {
              console.log("Returned place contains no geometry");
              return;
            }
            var icon = {
              url: place.icon,
              size:  this.google.maps.Size(71, 71),
              origin:  this.google.maps.Point(0, 0),
              anchor:  this.google.maps.Point(17, 34),
              scaledSize:  this.google.maps.Size(25, 25)
            };

            // Create a marker for each place.
            markers.push( this.google.maps.Marker({
              map: map,
              icon: icon,
              title: place.name,
              position: place.geometry.location
            }));

            if (place.geometry.viewport) {
              // Only geocodes have viewport.
              bounds.union(place.geometry.viewport);
            } else {
              bounds.extend(place.geometry.location);
            }
          });
          map.fitBounds(bounds);
        });
      }

    //only called after the component has been rendered and grabs a reference to the DOM component 
    loadMap(){

        if(this.props && this.props.google){
            //check if google is available 
            const{google}=this.props;
            const maps=google.maps;
            const mapRef=this.refs.map;

            //reference to the actual DOM element 
            const node=ReactDOM.findDOMNode(mapRef);

            let{zoom}=this.props;
            const{lat,lng,name}=this.state.currentLocation;
            const center= new maps.LatLng(lat,lng,name);
            const mapConfig=Object.assign(
                {},
                {
                    center:center,
                    zoom:zoom
                }
            );
            this.map= new maps.Map(node,mapConfig);
            this.initAutocomplete();
        }
    }

    renderChildren(){
        const {children}=this.props;

        if(!children) return;

        return React.Children.map(children, c=>{
            if(!c) return;
            return React.cloneElement(c,{
                map: this.map,
                google:this.props.google,
                mapCenter: this.state.currentLocation
            });
        });
    }

    render(){
        const style=Object.assign({},mapStyles.map);
        return(
            <div>
                <div style={style} ref="map">
                Loading map . . .
                </div>
                {this.renderChildren()}
            </div>
        );
    }

}

export default CurrentLocation;

CurrentLocation.defaultProps={
    zoom:20,
    initialCenter:{
        lat:40.723839,
        lng: -104.105515,
        name:'hey'
    },
    centerAroundCurrentLocation:false,
    visible:false
};
