import React, { Component } from 'react';
import './App.css';
import './MapContainer.js'
import Landing from './components/landingpage'
import {Layout, Header, Navigation, Drawer, Content } from 'react-mdl';
import { MapContainer } from './MapContainer.js';
//import { Map, GoogleApiWrapper } from 'google-maps-react';
import {Map,GoogleApiWrapper, InfoWindow ,Marker} from 'google-maps-react';
import CurrentLocation from './Map';

const mapStyles = {
    width: '100%',
    height: '100%'
  };

class App extends Component {
    state={
        showingInfoWindow:false, //hids or shows the info window
        activeMarker:{},//show active marker upon click
        selectedPlace:{} //show the infowindow to the selected place upon a marker
      };
      //used to show info window which is a component of google maps react library. Give us the ability for pop up window showing details of 
      //clicked place or marker 
      //prop is a boolean that contrls the infowindow, show -> true, hide->false
      onMarkerClick=(props,marker,e)=>
        this.setState({
          showingInfoWindow:true,
          activeMarker: marker,
          selectedPlace:props
        });

        //closes the infoWindow once a user clicks the button
        onClose=props =>{
          if(this.state.showingInfoWindow){
            this.setState({
              showingInfoWindow:false,
              activeMarker: null,
              selectedPlace:null
            });
          }
        };
  render() {

    let show_name;
  if(this.state.showingInfoWindow){
    show_name=(<div>
      <h4>{this.state.selectedPlace.mapCenter.name_loc}</h4>
    </div>)
  }else{
    show_name=(<div>
      <h4>{""}</h4>
    </div>)
  }


    return (

<div className="demo-big-content">
<Layout>
    <Header className ="header-color" title="Road Trip Advisor" scroll>
        <Navigation>
            <a href="/">Home</a>
            <a href="/">Plan a trip</a>
            <a href="/">Sign Up</a>
            <a href="/">Sign In</a>
        </Navigation>
    </Header>
    <Drawer title="Menu">
        <Navigation>
        <a href="/">Home</a>
        <a href="/">Plan a trip</a>
        <a href="/">Sign Up</a>
        <a href="/">Sign In</a>
        </Navigation>
    </Drawer>
    <Content>
        <div className="page-content">
          <Landing/>
          <MapContainer/>
        </div>

    </Content>
</Layout>
</div>
    );
  }
}
export default App
