<div style={{ height: 120 }}>
  <Popover
    id="popover-basic"
    placement="right"
    positionLeft={200}
    positionTop={50}
    title="Popover right"
  >
    And here's some <strong>amazing</strong> content. It's very engaging. right?
  </Popover>
</div>
        );
      });
