import React, { Component } from "react";
import "./App.css";
class Event extends Component {

  render() {
    var eventStyle = {
      padding: "10px",
      margin: "5px",
      width: "300px",
      wordWrap: "break-word",
      fontSize: 15,
      backgroundColor: this.props.color,
      textAlign: "left",
      display: "inline-block"
    };

    var buttonpos = {
      position:"right-align"
    };

  let status=this.props.showButton;

    return (
      <div>

      {status? ( 
        <div><p style={eventStyle}>{this.props.event} {<button class="fa fa-trash" style={buttonpos} onClick={()=>this.props.handleClick(this.props.event)}></button>}</p>     
        </div>
        ):(<p style={eventStyle}>{this.props.event}</p>
       )}

      </div>
    );
  }
}

export default Event;
