import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import { Popover } from 'bootstrap';
import Dropdown from 'react-dropdown-multiselect';

class App extends React.Component{

    constructor(props){
      super(props);

      this.handleChange=this.handleChange.bind(this);
      this.handleSubmit=this.handleSubmit.bind(this);
      this.state={
        view: false,
        value:'',
        selected:{value:'two',label:'Two'}
      };
    }

    handleSelect(e){
      console.log("you selected ",e.label)
      this.setState({selected:e})
    }
    handleChange(e){
      this.setState({value: e.target.value});
    }

    handleSubmit(e){
      alert(this.state.value);
      e.preventDefault();
    }
    render(){
      const options=[
        { value: 'one',label:'One'},
        { value: 'two',label:'Two'},
        {
          type:'group', name:'group1', items:[
            {value:'three',label:'Three'},
            {value:"four",label:"Four"}
          ]
        }
      ]
      let defaultOption = this.state.selected
        return(
            <div>
                <h1>Meal Preferences Application</h1>
                <button onClick={this.viewOption}>Select Options</button>
            <form onSubmit={this.handleSubmit}>
              <label>
                Meal Pref:
                <input type="text" value={this.state.value} onChange={this.handleChange}/>
              </label>
              <input type="submit" value="Submit" />
           <Dropdown options={options} onChange={this.handleSelect.bind(this)} value={defaultOption} placeholder="Select an option"/>
            </form>
            </div>
        );
    }
}

export default App;
