import React, { Component } from "react";
import { Form, Input } from "reactstrap";
import { Button, FormGroup, FormControl, ControlLabel } from "react-bootstrap";

import "bootstrap-daterangepicker/daterangepicker.css";
import { Redirect } from "react-router-dom";
import MapContainer from "./MapContainer.js";
import LocationSearchInput from "./LocationSearchInput";

class Landing extends Component {
    constructor(props) {
        super(props);

        this.state = {
          email: "",
          password: "",
          redirect:false,
          loginClick:false
        };
      }

      validateForm() {
          console.log("validate form method")
          if(this.state.email.length > 0 && this.state.password.length > 0){
          this.setState({redirect:true});
          return true;
          }
          return false;

      }

      renderRedirect() {
          if(this.state.redirect){
            return <Redirect to={{ pathname: "/home"}} />;
          }
      }

      handleChange = event => {
        this.setState({
          [event.target.id]: event.target.value
        });
      }

      handleSubmit = event => {
        event.preventDefault();
      }
 render(){
    var positionStyle={
        display: "flex",
        flexDirection: "column",
       alignItems:"center",
        justifyContent:"center",
  }
  var inputStyle={ opacity:0.7}

    return (
      <div class="here otherBack">
        <div className="text-center">
          <div className="landingpage-style">
            <h1 className="roadTripAdvisor">Road Trip Advisor</h1>
            <p className="roadTripAdvisor">
              {" "}
              Share Road Trip Advisor with your friends!
            </p>
            </div>
        </div>

    <div style={positionStyle} class="text-center">
   <div class="col-sm-offset-12">

        <div class="col-sm-12">
          <FormGroup controlId="email" bsSize="large">
            <ControlLabel>Email</ControlLabel>
            <FormControl
            style={inputStyle} 
              autoFocus
              type="email"
              value={this.state.email}
             onChange={this.handleChange}
            />
          </FormGroup>
          </div>
          <div class="col-sm-12">
          <FormGroup controlId="password" bsSize="large">
            <ControlLabel>Password</ControlLabel>
            <FormControl style={inputStyle} 
              value={this.state.password}
              onChange={this.handleChange}
              type="password"
            />
          </FormGroup>
          {this.renderRedirect()}
          <button class="btn btn-warning pl-5 pr-5"
            block
            bsSize="large"
            onClick={this.validateForm}
            type="button"
          > Login
          </button>
          </div>

          </div>
          <p></p>
          <div> <button  class="btn btn-warning pl-5 pr-5"
            block
            bsSize="large"
            type="submit"
          > Sign Up
          </button></div>
      </div>
      </div>
    );
  }
}

export default Landing;
