import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import Select from 'react-select';
import Filter from "./filter.js";


class App extends React.Component{

    constructor(props){
      super(props);

      // this.handleChange=this.handleChange.bind(this);
      // this.handleSubmit=this.handleSubmit.bind(this);
      this.handleOnSelect=this.handleOnSelect.bind(this);
      this.state={
        // view: false,
        // value:'',
        selected:[],
        count:0
      };
    }

    handleOnSelect(selection){
      console.log(this.state.selected)
      console.log(this.state.count)
      // if(!selection){
      //   alert('enter valild input')
      // }else if(this.state.selected.indexOf(selection)>-1){
      //   alert('removing')

      // }
      this.setState((prevState)=>{

        return{
          selected:selection,
          count:prevState.count+1    
        };
      })

    }

    // handleChange(e){
    //   this.setState({value: e.target.value});
    // }

    // handleSubmit(e){
    //   alert(this.state.value);
    //   e.preventDefault();
    // }
    render(){
        return(
            <div>
            <h1>Meal Preferences Application</h1>
            <Filter name='meal_type' choices= {[ 
          {value: '0',label:'Please select kind of meal'},    
          {value: '1',label:'All Food & Drinks'},
          {value:'2',label:'Bars & Drinks'},
          {value:'3',label:'Burgers & BBQ'},
          {value: '4',label:'Delis & Bakeries'},
          {value:'5',label:'Coffee & Tea'},
          {value:'6',label:'Diners & Breakfast'},
          {value: '7',label:'Ice cream & Desserts'},
          {value:'8',label:'Vegeterian & Healthy Food'},
          {value:'9',label:'Kosher'},
          {value: '10',label:'Halal'},
          {value:'11',label:'Wineries & Distelliers'},
          {value:'12',label:'Resturants'},




          ]}></Filter>
            </div>
        );
    }
}

export default App;
