import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import Select from 'react-select';
import Filter from "./filter.js";
import StarRating from "./starRatings.js";
import RangeSlider from './rangeSlider.js';
import Child from './child.js';


class App extends React.Component{

    constructor(props){
      super(props);

      this.state={
        selected:[],
        count:0,
        data:['']
      };
      //this.childHandler=this.childHandler.bind(this)
      this.selectedHandler=this.selectedHandler.bind(this)
    }

    // childHandler(datafromchild){
    //   this.setState({
    //     data:datafromchild
    //   })
    // }

    //to get data back from Child 
    selectedHandler(data){
      this.setState({
        selected:data
      })
    }


    render(){
      const money_label={0:'$',50:'$$',100:'$$$'}
      const distance_label={0:'Near',50:'Far',100:'Furthest'}
      const dinning_options=[ 
        {value: '1',label:'All Food & Drinks'},
        {value:'2',label:'Bars & Drinks'},
        {value:'3',label:'Burgers & BBQ'},
        {value: '4',label:'Delis & Bakeries'},
        {value:'5',label:'Coffee & Tea'},
        {value:'6',label:'Diners & Breakfast'},
        {value: '7',label:'Ice cream & Desserts'},
        {value:'8',label:'Vegeterian & Healthy Food'},
        {value:'9',label:'Kosher'},
        {value: '10',label:'Halal'},
        {value:'11',label:'Wineries & Distelliers'},
        {value:'12',label:'Resturants'},  
        ]
        return(
            <div>
            {/* <Child action={this.childHandler}/>
            <p>{this.state.data}</p> */}

            <h1>Meal Preferences Application</h1>
            <Filter name='meal_type' placeholder= 'Meal Type' action={this.selectedHandler} choices= {dinning_options}></Filter>       
              {
              this.state.selected.map(select=> {
                return(
                  <div key={select.value}>
                  <p>{select.label}</p>
                  </div>
                )
              })
            }
          <p>Price range</p>
          <RangeSlider name='money_range' label={money_label}/>
          <br/>
          <p>Distance to travel from Location</p>
          <RangeSlider name='distance_range' label={distance_label}/>
          <br/>
          <p>Reviews</p>
          <StarRating/>
            </div>

        );
    }
}

export default App;
