import logo from './logo.svg';
import './App.css';
import Map from './Map.js';
import MF from './MealMap.js';
import MealCard from './MealCard.js';
import Content from './MealCard.js';
import {Button} from 'react-bootstrap';
import {connect} from "react-redux";
import MealPref from "./MealPref"

class App extends React.Component{
  constructor(props){
      super(props);

      this.state={
        selected:["1"],
        count:0,
        price_range:'any',//any price range
        distance:'any',
        ratings:'any',
        search_result:[]
      };
      //this.childHandler=this.childHandler.bind(this)
      this.abc=this.abc.bind(this)
      this.PriceRange_Handler=this.PriceRange_Handler.bind(this)
      this.distance_Handler=this.distance_Handler.bind(this)
      this.ratings_Handler=this.ratings_Handler.bind(this)
    }

    //to get data back from Child 
    abc(){
      console.log(this.state.selected);


    }

    PriceRange_Handler(price){
      this.setState({
        price_range:price
      })
    }

    distance_Handler(dist){
      this.setState({
        distance:dist
      })
    }

    ratings_Handler(ratings){
      this.setState({
        ratings:ratings
      })
    }

    render(){
      const _this = this;
  return(
        <div>
           <MealCard color="#FF6663" selected={this.state.selected} 
           price={this.state.price_range} radius={this.state.distance}
           ratings={this.state.ratings} onClick={_this.abc}
            ></MealCard>
              <Button size="sm" onClick={_this.abc}>Submit</Button>

           {/* <MF search={this.state.selected} price={this.state.price_range} 
            reviews={this.state.ratings} radius={this.state.distance}

          /> */}
          </div>



        );
    }
}

export default App;

