import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import Select from 'react-select'


class Filter extends React.Component{

    constructor(props){
      super(props);

      // this.handleChange=this.handleChange.bind(this);
      // this.handleSubmit=this.handleSubmit.bind(this);
      this.handleOnSelect=this.handleOnSelect.bind(this);
      this.state={
        // view: false,
        // value:'',
        selected:[],
        name:'',
        count:0
      };
    }

    handleOnSelect(selection){
      console.log(this.state.selected)
      console.log(this.state.count)
      // if(!selection){
      //   alert('enter valild input')
      // }else if(this.state.selected.indexOf(selection)>-1){
      //   alert('removing')

      // }
      this.setState((prevState)=>{

        return{
          selected:selection,
          count:prevState.count+1    
        };
      })

    }

    // handleChange(e){
    //   this.setState({value: e.target.value});
    // }

    // handleSubmit(e){
    //   alert(this.state.value);
    //   e.preventDefault();
    // }
    render(){
        const options= this.props.choices;
        const placeholder=this.props.placeholder;
        return(
            <div>
                <h1>Count={this.state.selected.length}</h1>
                {/* <button onClick={this.viewOption}>Select Options</button>
            <form onSubmit={this.handleSubmit}>
              <label>
                Meal Pref:
                <input type="text" value={this.state.value} onChange={this.handleChange}/>
              </label> */}
              {/* <input type="submit" value="Submit" /> */}
            {/* </form> */}

            <Select 
            style={{width: '20%'}} autosize={false}
            options={options} placeholder={placeholder} isMulti isClearable isSearchable 
            onChange={this.handleOnSelect}/>

            {
              this.state.selected.map(select=> {
                return(
                  <div key={select.value}>
                  <p>{select.label}</p>
                  </div>
                )
              })
            }
            </div>
        );
    }
}

export default Filter;
